
/*! chartjs-shim.min.js | Fallback placeholder when CDN Chart.js is unavailable */
(function(){
  if (window.Chart) return;
  function drawPlaceholder(ctx, msg){
    try {
      var c = ctx && (ctx.canvas || ctx) ;
      var g = (c.getContext ? c.getContext('2d') : ctx);
      if(!g) return;
      var w = (c.width||400), h=(c.height||150);
      g.clearRect(0,0,w,h);
      g.save();
      g.fillStyle = '#555';
      g.font = '14px system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial';
      g.fillText(msg || 'Chart.js tidak tersedia (offline mode).', 10, 24);
      g.restore();
    } catch(e){}
  }
  function Chart(ctx, cfg){
    this.config = cfg||{};
    this.data = (cfg&&cfg.data)||{labels:[], datasets:[]};
    this.update = function(){ drawPlaceholder(ctx, 'Chart.js tidak tersedia (offline mode).'); };
    this.destroy = function(){};
    // initial draw
    this.update();
  }
  window.Chart = Chart;
})();
